export const  WingoFeaturedImageList:FC<WingoFeaturedImageListProp> = ({title, list}) => {
    const {getStyles} = useTheme();
  return <div data-featured-image-list-component>  
     
      { (typeof title == 'string')?<Typography   variant="h2" className="mb-6">{title}</Typography>:title}
   
     <div data-featured-image-list>
        {
            list?.map((content:WingoFeaturedImageListItemProp)=>{
                return <FeaturedImage {...content}/>
            })
        }
    </div>
  </div>  
}

WingoFeaturedImageList.defaultProps = {
    title: "Feature Heading",
    list: []
}

 
