import {NavBar, Footer as CoreFooter} from 'reactwiind';
import { ComponentProps, FC } from 'react';
import {MindWingoYellow,Icon} from "rw-assets";
import classNames from 'classnames';


export interface SocialProps {
    href?: string;
    icon?: string;
    className?: string;
    color?:string;
}

export const socialIcons = {
    linkedin:"linkedin",
    youtube:"youtube",
    facebook:"facebook",
    instagram:"instagram"
}

export const Social = ({list, fill="fill-secondary"}:{list?:Array<SocialProps>, fill?:string})=> {
    return <div className="flex ">
        {
            list?.map(({href,icon, color=fill, className})=>{
                return <a target="_blank" rel="noreferrer" className={classNames("px-2",className)} href={href}><Icon icon={icon} fill={color}/></a>
            })
        }
     </div>
 }
 