import {NavBar, NavBarLinksProps, NavBarLogoProps } from 'reactwiind';
import { FC, ReactNode } from 'react';
 
export interface StandardHeaderProps extends NavBarLinksProps {
  logo: NavBarLogoProps;
  right: ReactNode;
}
 
export const  StandardHeader:FC<StandardHeaderProps> = ({links, logo, right}) => {
  return  <NavBar>
              <NavBar.Logo>{logo as ReactNode}</NavBar.Logo>
              <NavBar.Collapse>
                {links?<NavBar.Links links = {links} />:right}
              </NavBar.Collapse>
          </NavBar>
         
}