import { FC, ReactNode } from 'react';
import { Hero,Flex, Content, Button, ButtonProps, Spacer, ContentProps } from 'reactwiind';
import type {HeroProps} from "reactwiind"

export interface HeroContentProps extends HeroProps, ContentProps {
  item?: React.ElementType | ReactNode | null ;
  button?: ButtonProps;
}

export const HeroContent:FC<HeroContentProps> = ({ content, button, item, children, order = "order-last lg:order-last"}) => {
  return  <Hero>
              <Hero.Item>
                <Flex direction="flex-col" justifyContent="justify-center" alignItems = "items-center">
                  <Content content = { content }/>
                  <Spacer/> <Spacer/>
                  {button ? <Button { ...button } />: ''}
                  {children}
                  </Flex>
              </Hero.Item>   
  </Hero>
}