import { FC, ReactNode } from 'react';
import { Hero, Content, Button, ButtonProps, Spacer, ContentProps } from 'reactwiind';
import type {HeroProps} from "reactwiind"


export interface HeroContentItemProps extends HeroProps, ContentProps {
  item?: React.ElementType | ReactNode | null ;
  button?: ButtonProps;
}

export const HeroContentItem:FC<HeroContentItemProps> = ({ content, button, item, order = "order-last lg:order-last", ...rest}) => {
  return  <Hero {...rest}>
              <Hero.Item>
                  <Content content = { content }/>
                  <Spacer/> <Spacer/>
                  {button ? <Button { ...button } />: ''}
              </Hero.Item> 
              <Hero.Item order = { order }>
                  { item as ReactNode }
              </Hero.Item>    
            </Hero>
}