import { FC } from 'react';
import { Grid, CardIconText, CardIconTextProps, BaseStyle, GridBaseStyle } from 'reactwiind';

export interface CardIconTextListProp extends BaseStyle, GridBaseStyle {
  list?: Array<CardIconTextProps>;
  className?: string;
  item?: CardIconTextProps;
} 

export const CardIconTextList:FC<CardIconTextListProp> = ({ list = [],item={}, ...rest }) => {
   
  return  <Grid {...rest}>
            {
              list.map(({ ...cardProps }: CardIconTextProps) => {
                return  <CardIconText {...{...item, ...cardProps}} />;
              })
            }
  </Grid>
}                