import { FC, ReactNode } from 'react';
import classNames from 'classnames';
import {BoyAlphabets, PlaceholderIcon, PlaceholderImage} from "rw-assets";
import {Hero, Container, useTheme, Typography, IconText, type IconTextProps} from 'reactwiind';
import { gets} from "../base";  

export interface IconTextListProp {
  list?: Array<IconTextProps>;
  className?: string;
  col?: string;
} 

const DefaultIcon = () => <svg width="50" height="50">
<circle cx="30" cy="20" r="80" stroke="#000" stroke-width="4" fill="#000000" />
</svg> 

const defaultList: Array<IconTextProps> = [
    {text:"Sample icon text", icon:PlaceholderIcon},
    {text:"Sample icon text", icon:PlaceholderIcon},
    {text:"Sample icon text", icon:PlaceholderIcon}
]


export const IconTextList:FC<IconTextListProp> = ({list = defaultList, col = "grid-cols-2" ,className}) => {

  return  <div className={classNames(gets?.("icontextlist.base") as string, col, className)}>
            {
              list.map(({icon, text=''}: IconTextProps) => {
                return  <IconText icon={icon} text={text}/>;
              })
            }
          </div>
}                