import {NavBar, Container, LinkProps, Link, Typography} from 'reactwiind';
import classNames from 'classnames';
import { ComponentProps, FC, ReactNode } from 'react';
 
export interface LinksProps {
  links?: Array<LinkProps>;
  heading?: string | ReactNode;
  col?: string;
}

 

const defaultLinks:Array<LinkProps> = [
    {text:"Menu Item", href:"", typographyProps:{color:"primary"}},
    {text:"Menu Item", href:"", typographyProps:{color:"primary"}},
    {text:"Menu Item", href:"", typographyProps:{color:"primary"}},
]
 
export const  Links:FC<LinksProps> = ({ links, heading, col="flex-col"}) => {

  return  <div className="flex flex-col">
            <Typography variant="h5" color="text-acccent">{heading}</Typography>
            <div className={classNames("flex",col)}>
                {links?.map(({text, href, icon, typographyProps:tp}:LinkProps)=>{
                return <Link href={href} icon={icon} typographyProps={tp}>{text}</Link>
                })}
            </div>
           </div>
}