export const baseWind = {
    icontextlist: {
        styles: { base: "p-2 align-center justify-center gap-4 grid"}
    },
    featuredimagetextlist: {
        styles: { heading: "py-4"}
    }
}

  
export const gets =  (path:string, prop:string=''): object | string =>{
    console.log("ret 1",path, ' ',prop);
    const props = path.split(".");
    props.splice(1,0, !!prop?"defaultProps":"styles");
    const addedStylePropPath = props.join(".");
    const ret = (addedStylePropPath
      .replace(/\[([^\[\]]*)\]/g, '.$1.')
      .split('.')
      .filter(t => t !== '')
      //@ts-ignore
      .reduce((prev, cur) => (prev as Record<string, unknown> && prev[cur] as Record<string, unknown>), baseWind)) as unknown as object | string;

      console.log("ret",path, ' ',ret);
    return ret as (string | object);
}
